import React, { useRef } from "react";
import emailjs from "emailjs-com";
import "./Contact.css"; // your existing styles

const Contact = () => {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    // Set the time before sending
    const timeInput = form.current.querySelector('input[name="time"]');
    if (timeInput) {
      timeInput.value = new Date().toLocaleString();
    }

    emailjs
      .sendForm(
        "service_uwtka7h",    // ‚úÖ Service ID
        "template_p3rkhdp",   // ‚úÖ Template ID
        form.current,
        "MZMvDnTuHmHuRYmfj"   // ‚úÖ Public Key
      )
      .then(
        (result) => {
          console.log("‚úÖ Email sent:", result.text);
          alert("Your message has been sent successfully!");
          e.target.reset();
        },
        (error) => {
          console.error("‚ùå Failed to send email:", error.text);
          alert("Failed to send message. Please try again.");
        }
      );
  };

  return (
    <section className="contact-section">
      <h2>CONTACT US</h2>
      <h3>If You Have Any Query, Feel Free To Contact Us</h3>

      <div className="contact-info">
        <div className="info-box">
          <div className="icon">üìû</div>
          <p>Call to ask any question</p>
          <strong>+012 345 6789</strong>
        </div>
        <div className="info-box">
          <div className="icon">‚úâÔ∏è</div>
          <p>Email to get free quote</p>
          <strong>info@quintessencesearchlabs.in</strong>
        </div>
      </div>

      <form ref={form} onSubmit={sendEmail} id="contactForm">
        <input type="text" name="name" placeholder="Your Name" required />
        <input type="email" name="email" placeholder="Your Email" required />
        <textarea name="message" placeholder="Your Message" required></textarea>
        
        {/* Hidden time field for {{time}} */}
        <input type="hidden" name="time" />

        <button type="submit">Send Message</button>
      </form>
    </section>
  );
};

export default Contact;
