import React, { useState, useEffect } from "react";
import "./About.css";

const slides = [
  {
    title: "Our Vision",
    content:
      "To be the trusted partner driving the future of digital innovation globally, enabling our clients to stay ahead in a rapidly changing technology ecosystem.",
    align: "left",
  },
  {
    title: "Our Mission",
    content:
      "To accelerate technology adoption and digital evolution for enterprises by delivering innovative, high-quality engineering solutions and strategic consulting.",
    align: "right",
  },
];

function VisionMissionCarousel() {
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrent((prev) => (prev === slides.length - 1 ? 0 : prev + 1));
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  const prevSlide = () => {
    setCurrent((prev) => (prev === 0 ? slides.length - 1 : prev - 1));
  };

  const nextSlide = () => {
    setCurrent((prev) => (prev === slides.length - 1 ? 0 : prev + 1));
  };

  return (
    <div className="carousel-container">
      <button className="nav-btn left" onClick={prevSlide} aria-label="Previous Slide">
        &#10094;
      </button>

      {slides.map((slide, index) => (
        <div
          key={index}
          className={`slide ${index === current ? "active" : ""} ${slide.align}`}
        >
          <h2>{slide.title}</h2>
          <p>{slide.content}</p>
        </div>
      ))}

      <button className="nav-btn right" onClick={nextSlide} aria-label="Next Slide">
        &#10095;
      </button>
    </div>
  );
}

export default VisionMissionCarousel;
